mapper.select:
SELECT p.id, p.title, p.picture, p.class_id, p.content, p.member_id, p.company_id, p.status, p.hits, p.add_time, p.update_time, p.type, p.attribute, p.keywords, p.market_price, p.real_price, c.name as className, m.name as memberName, m.head_portrait, m.description, m.adviser_type FROM j_product p LEFT JOIN j_class c ON p.class_id = c.id LEFT JOIN j_member m on p.member_id = m.id LEFT JOIN j_area a on a.code = m.area_code
p.member_id = #{memberId,jdbcType=BIGINT}
AND p.status = #{status,jdbcType=TINYINT}
AND p.title like CONCAT('%',#{title},'%')
AND p.id = #{id}
AND m.area_code like CONCAT(#{areaCode},'%')
AND c.code like CONCAT(#{classCode},'%')
order by p.weight_value DESC,p.recommend DESC
